function params = displayParams%% For the CNI LCD display at the end of the bore.%% % CODE TO SAVE GAMMA TABLE%% Gamma table should be linear%    gamma = (ones(3,1)*(0:255)/255)';%    gammaTable = (ones(3,1)*(0:255))';% % % where to save the gamma file?%   pth = fullfile(vistadispRootPath, 'exptTools', 'displays','CNI_Stanford_screen');%   save(fullfile(pth, 'gamma'), 'gamma', 'gammaTable');%% Critical parametersparams.numPixels    = [1920 1080];params.dimensions   = [16/9*36.2 36.2]; % cmparams.distance     = 83.5;params.frameRate    = 60;params.cmapDepth    = 8;params.screenNumber = 1;% Descriptive parametersparams.position = 'Propixx default';params.stereoFlag = 0;% Display parameters to make accounting for mirrors easierparams.flipLR = 0;params.flipUD = 1;end